define({"topics":[{"title":"Adding default virtual backgrounds on Docker","shortdesc":"<p class=\"shortdesc\">By default, virtual background is enabled. You can choose which backgrounds are         available to users by default. Depending on your business needs, you can customize the         global library and prevent users from uploading custom background images. For more         information, refer to <a class=\"xref\" href=\"t_disable_background_upload.html\">t_disable_background_upload.html<\/a>.<\/p>","href":"t_configure_virtual_docker.html","attributes":{"data-id":"t_configure_virtual_docker"},"menu":{"hasChildren":false},"tocID":"t_configure_virtual_docker-d1897e1171","topics":[]},{"title":"Adding default virtual backgrounds on Kubernetes","href":"t_configure_virtual_k8s.html","attributes":{"data-id":"t_configure_virtual_k8s"},"menu":{"hasChildren":false},"tocID":"t_configure_virtual_k8s-d1897e1192","topics":[]}]});